KOKKOS_INLINE_FUNCTION void jacobi_parallel(KView<double ***> u, KView<double ***> unew, int n, int niter) {
for (int iter = 0; iter < niter; ++iter) {
parallel_for("Jacobi", range_tag1(2, n - 1) * range_tag2(2, n - 1)  * range_tag3(2, n - 1), KOKKOS_LAMBDA (const int i, const int j, const int k) {
unew(i, j, k) = 0.125 * (u(i - 1, j, k) + u(i + 1, j, k) + u(i, j - 1, k) +
u(i, j + 1, k) + u(i, j, k - 1) + u(i, j, k + 1) + u(i, j, k));
});
Kokkos::deep_copy(u, unew);
}
}

Kokkos::parallel_for("Jacobi loop", n*n*n,
Kokkos::MDRangePolicy<Kokkos::Rank<3>>({2, 2, 2}, {n - 1, n - 1, n - 1}),
