KOKKOS_INLINE_FUNCTION void jacobi_parallel(Kokkos::View<double***>::HostMirror u, Kokkos::View<double***>::HostMirror unew, const int n, const int niter) {
for (int iter = 0; iter < niter; ++iter) {
Kokkos::parallel_for("Jacobi Iteration", n, n, n, KOKKOS_LAMBDA(const int i, const int j, const int k) {
if (i > 0 && i < n - 1 && j > 0 && j < n - 1 && k > 0 && k < n - 1) {
unew(i, j, k) = 0.125 * (u(i - 1, j, k) + u(i + 1, j, k) + u(i, j - 1, k) + u(i, j + 1, k) + u(i, j, k - 1) + u(i, j, k + 1) + u(i, j, k));
}
});
Kokkos::deep_copy(u, unew);
}
}

