namespace Kokkos {
typedef View<double***, ExecutionSpace> ViewType;
void jacobiParallel(ViewType u, ViewType unew, int n, int niter) {
for (int iter = 0; iter < niter; ++iter) {
parallel_for("Jacobi iteration", RangePolicy<ExecutionSpace>(1, n - 1), KOKKOS_LAMBDA (const int k) {
for (int j = 1; j < n - 1; ++j) {
for (int i = 1; i < n - 1; ++i) {
unew(i, j, k) = 0.125 * (u(i - 1, j, k) + u(i + 1, j, k) + u(i, j - 1, k) + u(i, j + 1, k) + u(i, j, k - 1) + u(i, j, k + 1) + u(i, j, k));
}
}
}
});
u.assign(unew);
}
}
}

